$(document).ready(function(){
    $('.button-menu-mobile').click(function(a){
        $('.show').toggleClass('sidebar-enable');

        //check if show has atribute
        var attr = $('.show').attr('data-leftbar-compact-mode');
        //detect screen width
        var width = $(window).width();


        
    
        if (typeof attr !== 'undefined' && attr !== false) {
            $('.show').removeAttr('data-leftbar-compact-mode');
            console.log('remove compact mode');
        }else{
            $('.show').attr('data-leftbar-compact-mode', 'condensed');
        }
    })
})

$(window).resize(function(){
    var width = $(window).width();
    console.log(width);
    if(width < 750){
        $('.show').attr('data-leftbar-compact-mode', 'condensed');
    }else{
        $('.show').removeAttr('data-leftbar-compact-mode');
    }
});
